const express = require('express');
const app = express();

// Rota para listar todas as tarefas do Kanban
app.get('/tasks', (req, res) => {
  // Lógica para buscar todas as tarefas do banco de dados
  // e retornar como resposta
  res.json({ tasks: [] });
});

// Rota para criar uma nova tarefa no Kanban
app.post('/tasks', (req, res) => {
  // Lógica para criar uma nova tarefa no banco de dados
  // com base nos dados enviados na requisição
  res.json({ message: 'Tarefa criada com sucesso' });
});

// Rota para atualizar uma tarefa do Kanban
app.put('/tasks/:id', (req, res) => {
  const taskId = req.params.id;
  // Lógica para atualizar a tarefa com o ID fornecido
  // com base nos dados enviados na requisição
  res.json({ message: `Tarefa ${taskId} atualizada com sucesso` });
});

// Rota para excluir uma tarefa do Kanban
app.delete('/tasks/:id', (req, res) => {
  const taskId = req.params.id;
  // Lógica para excluir a tarefa com o ID fornecido
  res.json({ message: `Tarefa ${taskId} excluída com sucesso` });
});

// Iniciar o servidor na porta 3000
app.listen(3000, () => {
  console.log('Servidor rodando na porta 3000');
});
